pragma solidity ^0.5.0;

contract HelloWorld {

    // define variables
    mapping(address => bool)  public authorizedSenders;
    address  public  owner;
    mapping(int64 => bytes32)  public hashChain;


    // define events
    event newHashRecorded(int64 indexed key, bytes32 indexed hash);
    event authorizationGranted(address indexed accountAddress);

    // constructor
    constructor() public {
        owner = msg.sender;
        authorizedSenders[owner] = true;
    }


    // functions
    function() external {}

    function authorize(address user) public {
        require(msg.sender == owner, "Only the contract owner can authorize users");
        authorizedSenders[user] = true;

        emit authorizationGranted(user);
    }

    function isAuthorized(address user) external view returns (bool authorized){
        return authorizedSenders[user];
    }



    function record(int64 date, bytes32 hash) external {
        require(authorizedSenders[msg.sender] == true);
        hashChain[date] = hash;

        emit newHashRecorded(date, hash);
    }

    function getHash(int64 date) external view returns (bytes32){
        return hashChain[date];
    }

}
